import React, {useContext, useEffect, useState} from 'react';
import {Button, Modal} from "react-bootstrap";
import InputGroup from 'react-bootstrap/InputGroup';
import Form from 'react-bootstrap/Form';
import Dropdown from 'react-bootstrap/Dropdown';
import {Context} from "../index";

const CreateBrand = () => {
    const [show, setShow] = useState(false);
    const [type, setType] = useState(false);
    const [selectedType, setSelectedType] = useState(null);

    const handleTypeSelect = (type) => {
        setSelectedType(type);
    };
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    const {device} = useContext(Context)
    return (
        <>
            <Button variant="primary" onClick={handleShow}>
                Добавити новий пристрій
            </Button>

            <Modal show={show} onHide={handleClose}>
                <Modal.Header closeButton>
                    <Modal.Title>Добавлення пристрою</Modal.Title>
                </Modal.Header>
                <Modal.Body>

                    <Dropdown className='my-4'>
                        <Dropdown.Toggle variant="success" id="dropdown-basic">
                            {selectedType ? selectedType.name : 'Виберіть тип'}
                        </Dropdown.Toggle>

                        <Dropdown.Menu>
                            {device.types.map((type) => (
                                <Dropdown.Item key={type.id} onClick={() => handleTypeSelect(type)}>
                                    {type.name}
                                </Dropdown.Item>
                            ))}
                        </Dropdown.Menu>
                    </Dropdown>


                    <Dropdown className='my-4'>
                        <Dropdown.Toggle variant="success" id="dropdown-basic">
                            {selectedType ? selectedType.name : 'Виберіть бренд'}
                        </Dropdown.Toggle>

                        <Dropdown.Menu>
                            {device.brands.map((type) => (
                                <Dropdown.Item key={type.id} onClick={() => handleTypeSelect(type)}>
                                    {type.name}
                                </Dropdown.Item>
                            ))}
                        </Dropdown.Menu>
                    </Dropdown>


                    <InputGroup className="mb-3">
                        <InputGroup.Text id="inputGroup-sizing-default">
                            Введіть назву типу
                        </InputGroup.Text>
                        <Form.Control
                            aria-label="Default"
                            aria-describedby="inputGroup-sizing-default"
                            aria-placeholder='Введіть назву типу'
                            onChange={(event) => setType(event.target.value)}
                        />
                    </InputGroup>
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={handleClose}>
                        Close
                    </Button>
                    <Button variant="primary" onClick={handleClose}>
                        Save Changes
                    </Button>
                </Modal.Footer>
            </Modal>
        </>
    );
};

export default CreateBrand;
