import {makeAutoObservable} from "mobx";

export default class DeviceStore {
    constructor() {
        this._types = [
            'Холодильники',
            'телефони',
            'ноутбуки',
            'планшети',
        ]
        this._brands = [
            'Apple',
            'Samsung',
            'Xiaomi',
        ]
        this._devices = []

        makeAutoObservable(this)
    }
    setTypes(type) {
        this._types = type

    }
    setBrands(bool) {
        this._brands = bool
    }
    setDevices(user) {
        this._devices = user
    }
    get types () {
        return this._types
    }
    get brands() {
        return this._brands
    }
    get devices() {
        return this._devices
    }
}
