import React, { useContext, useState } from 'react';
import { Button, Card, Container, Form, NavLink, Row } from 'react-bootstrap';
import { useLocation, useNavigate } from 'react-router-dom';
import { LOGIN_ROUTE, REGISTRATION_ROUTE } from '../utils';
import { Context } from '../index';

const AuthPage = () => {
    const { user } = useContext(Context);
    const location = useLocation();
    const history = useNavigate();
    const isLogin = location.pathname === LOGIN_ROUTE;
    const [password, setPassword] = useState('');
    const [email, setEmail] = useState('');

    return (
        <Container
            className="d-flex justify-content-center align-items-center"
            style={{ height: 'calc(100vh - 54px)' }}
        >
            <Card style={{ width: '18rem' }} className="p-5">
                <h2 className="m-auto">{isLogin ? 'Авторизация' : 'Регистрация'}</h2>
                <Form className="d-flex flex-column">
                    <Form.Control
                        className="mt-3"
                        placeholder="Введите ваш email..."
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                    />
                    <Form.Control
                        className="mt-3"
                        placeholder="Введите ваш пароль..."
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        type="password"
                    />
                    <Row className="d-flex justify-content-between mt-3 pl-3 pr-3">
                        {isLogin ? (
                            <div>
                                Нет аккаунта? <NavLink to={REGISTRATION_ROUTE}>Зарегистрируйся!</NavLink>
                            </div>
                        ) : (
                            <div>
                                Есть аккаунт? <NavLink to={LOGIN_ROUTE}>Войдите!</NavLink>
                            </div>
                        )}
                        <Button variant="outline-success">{isLogin ? 'Войти' : 'Регистрация'}</Button>
                    </Row>
                </Form>
            </Card>
        </Container>
    );
};

export default AuthPage;
