import React, {useContext, useEffect, useState} from 'react';
import {Button, Col, FormText, Modal, Row} from "react-bootstrap";
import InputGroup from 'react-bootstrap/InputGroup';
import Form from 'react-bootstrap/Form';
import Dropdown from 'react-bootstrap/Dropdown';
import {Context} from "../../index";
import {createDevice, getAllBrands, getAllDevices, getAllTypes} from "../../http/deviceApi";
import {values} from "mobx";
import data from "bootstrap/js/src/dom/data";

const CreateBrand = () => {
    // const [selectedType, setSelectedType] = useState(null);
    // const [selectedBrand, setSelectedBrand] = useState(null);
    const [show, setShow] = useState(false);
    const [name, setName] = useState('');
    const [price, setPrice] = useState(0);
    const [file, setFile] = useState(null);
    const [info, setInfo] = useState([]);

    const {device} = useContext(Context)
    useEffect(() => {
        getAllTypes().then(data => device.setTypes(data)).catch(data => data.message)
        getAllBrands().then(data => device.setBrands(data)).catch(data => data.message)
    }, []);
    // const handleTypeSelect = (type) => setSelectedType(type);
    // const handleBrandSelect = (type) => setSelectedBrand(type);
    const selectedFile = (e) => {
        setFile(e.target.files[0])
        console.log(file)
    }
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    const addInfo = () => {
        setInfo([...info, {title: '', description: '', number: Date.now()}])
    }
    const removeInfo = (number) => {
        setInfo(info.filter(i => i.number !== number))
    }

    const changeInfo = (key, value, number) => {
        setInfo(info.map(i => i.number === number ? {...i, [key]: value} : i))
    }

    const postDevice = () => {
        const formData = new FormData()
        formData.append('name', name)
        formData.append('price', `${price}`)
        formData.append('img', file)
        formData.append('brandId', device.selectedBrand.id)
        formData.append('typeId', device.selectedType.id)
        formData.append('info', JSON.stringify(info))
        createDevice(formData).then(() => handleClose).catch(e => console.log(e))
    }

    const sdf = (data) => {
        console.log(data)
    }
    return (
        <>
            <Button variant="primary" onClick={handleShow}>
                Добавити новий пристрій
            </Button>

            <Modal
                show={show}
                onHide={handleClose}
                centered
            >
                <Modal.Header closeButton>
                    <Modal.Title id="contained-modal-title-vcenter">
                        Добавить устройство
                    </Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Form>
                        <Dropdown className="mt-2 mb-2">
                            <Dropdown.Toggle>{device.selectedType.name || "Выберите тип"}</Dropdown.Toggle>
                            <Dropdown.Menu>
                                {device.types.map(type =>
                                    <Dropdown.Item
                                        onClick={() => device.setSelectedType(type)}
                                        key={type.id}
                                    >
                                        {type.name}
                                    </Dropdown.Item>
                                )}
                            </Dropdown.Menu>
                        </Dropdown>
                        <Dropdown className="mt-2 mb-2">
                            <Dropdown.Toggle>{device.selectedBrand.name || "Выберите тип"}</Dropdown.Toggle>
                            <Dropdown.Menu>
                                {device.brands.map(brand =>
                                    <Dropdown.Item
                                        onClick={() => device.setSelectedBrand(brand)}
                                        key={brand.id}
                                    >
                                        {brand.name}
                                    </Dropdown.Item>
                                )}
                            </Dropdown.Menu>
                        </Dropdown>
                        <Form.Control
                            value={name}
                            onChange={e => setName(e.target.value)}
                            className="mt-3"
                            placeholder="Введите название устройства"
                        />
                        <Form.Control
                            value={price}
                            onChange={e => setPrice(Number(e.target.value))}
                            className="mt-3"
                            placeholder="Введите стоимость устройства"
                            type="number"
                        />
                        <Form.Control
                            className="mt-3"
                            type="file"
                            onChange={selectedFile}
                        />
                        <hr/>
                        <Button
                            variant={"outline-dark"}
                            onClick={addInfo}
                        >
                            Добавить новое свойство
                        </Button>
                        {info.map(i =>
                            <Row className="mt-4" key={i.number}>
                                <Col md={4}>
                                    <Form.Control
                                        value={i.title}
                                        onChange={(e) => changeInfo('title', e.target.value, i.number)}
                                        placeholder="Введите название свойства"
                                    />
                                </Col>
                                <Col md={4}>
                                    <Form.Control
                                        value={i.description}
                                        onChange={(e) => changeInfo('description', e.target.value, i.number)}
                                        placeholder="Введите описание свойства"
                                    />
                                </Col>
                                <Col md={4}>
                                    <Button
                                        onClick={() => removeInfo(i.number)}
                                        variant={"outline-danger"}
                                    >
                                        Удалить
                                    </Button>
                                </Col>
                            </Row>
                        )}
                    </Form>
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="outline-danger" onClick={handleClose}>Закрыть</Button>
                    <Button variant="outline-success" onClick={postDevice}>Добавить</Button>
                </Modal.Footer>
            </Modal>
        </>
    );
};

export default CreateBrand;
