import React, {useContext, useEffect} from 'react';
import {Button, Card, Col, Container, ListGroup, Row, Image} from "react-bootstrap";
import {Context} from "../index";
import DeviceItem from "../components/DeviceItem";
import {observer} from "mobx-react-lite";
import {getAllBrands, getAllDevices, getAllTypes} from "../http/deviceApi";

const ShopPage = observer (() => {
    const {device} = useContext(Context)

    useEffect(() => {
        getAllTypes().then(data => device.setTypes(data)).catch(data => data.message)
        getAllBrands().then(data => device.setBrands(data)).catch(data => data.message)
    }, []);

    return (
        <Container>
            <Row className="mt-4" >
                <Col md={3}>
                    <ListGroup className="border-success" style={{cursor: 'pointer'}}>
                        {device.types.map((types) => (
                            <ListGroup.Item style={{borderWidth: '2px',}} className="border-success">{types.name}</ListGroup.Item>
                        ))}

                    </ListGroup>
                </Col>
                <Col md={9}>
                    <div className="d-flex flex-row gap-2" style={{cursor: 'pointer'}}>
                        {device.brands.map((brand) => (
                            <Card style={{borderWidth: '2px', borderRadius: '20px'}} className="border-success text-success" key={brand.name}>
                                <Card.Body>{brand.name}</Card.Body>
                            </Card>
                        ))}
                    </div>

                    <Row className="d-flex">
                        {device.devices.map(device =>
                            <DeviceItem key={device.id} device={device} />
                        )}
                    </Row>
                </Col>

            </Row>
        </Container>
    );
});

export default ShopPage;
