import {makeAutoObservable} from "mobx";

export default class DeviceStore {
    constructor() {
        this._types = []
            // {id: 1, name: 'Холодильники'},
            // {id: 2, name: 'телефони'},
            // {id: 3, name: 'ноутбуки'},
            // {id: 4, name: 'планшети'},

        this._brands = []
        this._devices = []

        makeAutoObservable(this)
    }
    setTypes(type) {
        this._types = type

    }
    setBrands(brands) {
        this._brands = brands
    }
    setDevices(devices) {
        this._devices = devices
    }
    get types () {
        return this._types
    }
    get brands() {
        return this._brands
    }
    get devices() {
        return this._devices
    }
}
