import React, {useContext} from 'react';
import {Button, Card, Col, Container, ListGroup, Row} from "react-bootstrap";
import {Context} from "../index";

const ShopPage = () => {
    const {device} = useContext(Context)
    return (
        <Container>
            <Row className="mt-4" >
                <Col md={3}>
                    <ListGroup>
                        {device.types.map((types) => (
                            <ListGroup.Item>{types.name}</ListGroup.Item>
                        ))}

                    </ListGroup>
                </Col>
                <Col md={9}>
                    <div className="d-flex flex-row gap-2 ">
                        {device.brands.map((brand) => (
                            <Card className="" key={brand.name}>
                                <Card.Body>{brand.name}</Card.Body>
                            </Card>
                        ))}
                    </div>
                    <div className="d-flex flex-row flex-wrap gap-4 ">
                        {device.devices.map((device) => (
                            <Card style={{ width: '18rem' }} >
                                <Card.Img style={{height: '50%', width: '50%'}} variant="top" src={device.img} className=" " />
                                <Card.Body>
                                    <Card.Title>Смартфон Apple</Card.Title>
                                    <Card.Text>
                                        {device.name}
                                    </Card.Text>
                                </Card.Body>
                            </Card>
                            ))}
                    </div>
                </Col>

            </Row>
        </Container>
    );
};

export default ShopPage;
