import {makeAutoObservable} from "mobx";

export default class DeviceStore {
    constructor() {
        this._types = [
            // {id: 1, name: 'Холодильники'},
            // {id: 2, name: 'телефони'},
            // {id: 3, name: 'ноутбуки'},
            // {id: 4, name: 'планшети'},
        ]
        this._brands = [
            {id: 1, name: 'Apple'},
            {id: 2, name: 'Samsung'},
            {id: 3, name: 'Xiaomi'},
        ]
        this._devices = [
            {id: 1, name: "Iphone 12 pro", price: 25000, rating: 5, img: "https://i5.walmartimages.com/asr/efecddbd-aa8d-45e3-9743-b49b25471f40.bc852a00bbf5df1136e4bbc2fb435cda.jpeg"},
            {id: 2, name: "Iphone 12 pro", price: 25000, rating: 5, img: "https://i5.walmartimages.com/asr/efecddbd-aa8d-45e3-9743-b49b25471f40.bc852a00bbf5df1136e4bbc2fb435cda.jpeg"},
            {id: 3, name: "Iphone 12 pro", price: 25000, rating: 5, img: "https://i5.walmartimages.com/asr/efecddbd-aa8d-45e3-9743-b49b25471f40.bc852a00bbf5df1136e4bbc2fb435cda.jpeg"},
            {id: 4, name: "Iphone 12 pro", price: 25000, rating: 5, img: "https://i5.walmartimages.com/asr/efecddbd-aa8d-45e3-9743-b49b25471f40.bc852a00bbf5df1136e4bbc2fb435cda.jpeg"},
            {id: 5, name: "Iphone 12 pro", price: 25000, rating: 5, img: "https://i5.walmartimages.com/asr/efecddbd-aa8d-45e3-9743-b49b25471f40.bc852a00bbf5df1136e4bbc2fb435cda.jpeg"}
        ]

        makeAutoObservable(this)
    }
    setTypes(type) {
        this._types = type

    }
    setBrands(bool) {
        this._brands = bool
    }
    setDevices(user) {
        this._devices = user
    }
    get types () {
        return this._types
    }
    get brands() {
        return this._brands
    }
    get devices() {
        return this._devices
    }
}
