import React, {useContext, useEffect, useState} from 'react';
import {Button, Card, Col, Container, Image, Row} from "react-bootstrap";
import {Context} from "../index";
import {useParams} from "react-router-dom";
import {map} from "react-bootstrap/ElementChildren";
import bigStar from "../assets/Star 1.png"
import {getAllBrands, getAllDevices, getAllTypes} from "../http/deviceApi";
const DevicePage = () => {
    const [type, setType] = useState()
    const [brand, setBrand] = useState()
    const [devices, setDevices] = useState()

    const {device} = useContext(Context)

    useEffect(() => {
        getAllTypes().then(data => device.setTypes(data)).catch(data => data.message)
        getAllBrands().then(data => device.setBrands(data)).catch(data => data.message)
        getAllDevices().then(data => device.setDevices(data.rows)).catch(data => data.message)
    }, []);

    return (
        <Container>
            <Row  className="mt-5">
                <Col md={4}>
                    <Image width={300} height={300} src={device.img}/>
                </Col>
                <Col md={4}>
                    <Row className="d-flex justify-content-center">
                        <h2 className="d-flex justify-content-center text-white">{device.name}</h2>
                        <div
                            className="d-flex align-items-center justify-content-center"
                            style={{background: `url(${bigStar}) no-repeat center center`, width:210, height: 200, backgroundSize: 'cover', fontSize:64}}
                        >
                            {device.rating}
                        </div>
                    </Row>
                </Col>
                <Col md={4}>
                    <Card style={{ height: '300px', background: 'rgba(255, 255, 255, 0.9)', borderWidth: '2px'}} className="border-success d-flex justify-content-between px-5 py-4">
                        <h3 className="d-flex justify-content-center">від {device.price} грн.</h3>
                        <Button>Добавити в корзину</Button>
                    </Card>
                </Col>
            </Row>
            <Row className="d-flex flex-column m-3 gap-1">
                <h1 className="my-4 text-white">Характеристики</h1>
                {description.map((info, index) => (
                    <Row key={info.id} style={{background: index % 2 === 0 ? 'lightgray' : '', color: index % 2 === 0 ? '' : 'white', padding: '10px', borderRadius: '10px'}}>
                        {info.title}: {info.description}
                    </Row>
                ))}
            </Row>
        </Container>
    );
};

export default DevicePage;
