import React, {useContext, useEffect} from 'react';
import {Button, Card, Col, Container, ListGroup, Row, Image, Pagination} from "react-bootstrap";
import {Context} from "../index";
import DeviceItem from "../components/DeviceItem";
import {observer} from "mobx-react-lite";
import {getAllBrands, getAllDevices, getAllTypes} from "../http/deviceApi";
import data from "bootstrap/js/src/dom/data";

const ShopPage = observer (() => {
    const {device} = useContext(Context)

    useEffect(() => {
        getAllTypes().then(data => device.setTypes(data)).catch(data => data.message)
        getAllBrands().then(data => device.setBrands(data)).catch(data => data.message)
        getAllDevices(null, null, 1, 2).then(data => device.setDevices(data.rows)).catch(data => data.message)
    }, []);

    useEffect(() => {
        getAllDevices(device.selectedType.id, device.selectedBrand.id, device.page, 2).then(data => {
            device.setDevices(data.rows)
            device.setTotalCount(data.count)
        })
    }, [device.page, device.selectedBrand, device.selectedBrand]);

    let active = device.page;
    let items = [];
    const pageCount = Math.ceil(device.totalCount / device.limit)
    // console.log(pageCount)
    for (let number = 1; number <= pageCount; number++) {
        items.push(
            <Pagination.Item
                key={number}
                active={number === active}
                onClick={() => device.setPage(number)}
            >
                {number}
            </Pagination.Item>,
        );
    }

    return (
        <Container>
            <Row className="mt-4" >
                <Col md={3}>
                    <ListGroup className="border-success" style={{cursor: 'pointer'}}>
                        {device.types.map((types) => (
                            <ListGroup.Item
                                onClick={() => device.setSelectedType(types)}
                                style={{
                                    borderWidth: '2px',
                                    background: types.id === device.selectedType.id ? 'green' : '',
                                    color: types.id === device.selectedType.id ? 'white' : '',
                                    border: types.id === device.selectedType.id ? 'black' : '',
                                }}
                                className="border-black"
                                active={types.id === device.selectedType.id}

                            >{types.name}</ListGroup.Item>
                        ))}

                    </ListGroup>
                </Col>
                <Col md={9}>
                    <div className="d-flex flex-row gap-2" style={{cursor: 'pointer'}}>
                        {device.brands.map((brand) => (
                            <Card
                                onClick={() => device.setSelectedBrand(brand)}
                                style={{
                                    borderWidth: '2px',
                                    borderRadius: '20px',
                                    backgroundColor: brand.id === device.selectedBrand.id ? 'green' : '',
                                    color: brand.id === device.selectedBrand.id ? 'white' : 'green',
                                    border: brand.id === device.selectedBrand.id ? 'none' : '',
                                }}
                                className="border-success"
                                key={brand.id}
                            >
                                <Card.Body>{brand.name}</Card.Body>
                            </Card>

                        ))}
                    </div>

                    <Row className="d-flex">
                        {device.devices.map(device =>
                            <DeviceItem key={device.id} device={device}/>
                        )}

                    </Row>
                    <Pagination className="mt-3">
                        {pages.map(page =>
                            <Pagination.Item
                                key={page}
                                active={device.page === page}
                                onClick={() => device.setPage(page)}
                            >
                                {page}
                            </Pagination.Item>
                        )}
                    </Pagination>
                </Col>

            </Row>
        </Container>
    );
});

export default ShopPage;
