import React, {useContext} from 'react';
import {Card, Col, Image, Row} from "react-bootstrap";
import {Context} from "../index";
import {useParams} from "react-router-dom";
import {map} from "react-bootstrap/ElementChildren";
import bigStar from "../assets/Star 1.png"
const DevicePage = () => {
    const device =  {id: 1, name: "Iphone 12 pro", price: 25000, rating: 5, img: "https://i5.walmartimages.com/asr/efecddbd-aa8d-45e3-9743-b49b25471f40.bc852a00bbf5df1136e4bbc2fb435cda.jpeg"}
    const {id} = useParams()

    return (
        <Row>
            <Col md={4}>
                <Image width={300} height={300} src={device.img}/>
            </Col>
            <Col md={4}>
                <Row>
                    <h2>{device.name}</h2>
                    <div
                        className="d-flex align-items-center justify-content-center"
                        style={{background: `url(${bigStar}) no-repeat center center`, width:210, height: 200, backgroundSize: 'cover', fontSize:64}}
                    >
                        {device.rating}
                    </div>
                </Row>
            </Col>
            <Col md={4}>
                <Card>
                    від {device.price} грн.
                </Card>
            </Col>
        </Row>
    );
};

export default DevicePage;
